import DefaultLayout from '../components/layouts/DefaultLayout';
import Link from 'next/link'
import style from './index.scss';


function fetchUrl(url) {
	return fetch(url).then(r => r.json());
}
/* This route gets hit by default, e.g. your-site.com */
const DefaultPage = () => {
	return (
		<DefaultLayout>
			<h1>Anders Killander</h1>
			<ul>
				<li>
					<Link href="/welcome"><a>Welcome</a>
					</Link>
				</li>
				<li>
					<Link href="/about"><a>About</a></Link>
				</li>
				<li>
					<Link href="/blog"><a>Blog</a></Link>
				</li>
			</ul>
		</DefaultLayout>
	);
};

/* Before rendering, we are calling our internal API endpoint (server).
This call is hitting the code we wrote in 'pages/api/page/[slug].js' */
DefaultPage.getInitialProps = async ({ query }) => {
	/* {query} is an object we receive within this method from Next,
	  so that we can retrieve the slug (e.g. '/welcome') coming with the route */
	const { page } = query;
	/* Calling our internal api endpoint so that we can fetch Storyblok content
	  server-side (we are on client-side here) */
	const data = await fetchUrl(`http://localhost:3000/api/page/${page}`);
	/* Returning the data back into SlugPage as props */
	return { data }; /* This is the same as { data: data } */
};


export default DefaultPage;
